<script>
export default {
    data() {
        return {
            langsMenuIsActive: false,
            idOfActiveLang: 0,
            currencyWindowOpen: false,
            activeCurrency: 'Eur',
            subMenuIsActive: false,
            menuIsActive: false,
        }
    },
    props: ['headerIsFull'],
    methods: {
        showLangs() {
            if (this.langsMenuIsActive == false) {
                this.langsMenuIsActive = true
            } else {
                this.langsMenuIsActive = false
            }
        },
        changeActiveLang(event) {
            if (event.currentTarget.id == 0) {
                this.idOfActiveLang = 0
                for (let item of document.querySelectorAll('.user-nav-lang__list-item')) {
                    item.classList.remove('user-nav-lang__list-item--active')
                }
                event.currentTarget.classList.add('user-nav-lang__list-item--active')
            } else if (event.currentTarget.id == 1) {
                this.idOfActiveLang = 1
                for (let item of document.querySelectorAll('.user-nav-lang__list-item')) {
                    item.classList.remove('user-nav-lang__list-item--active')
                }
                console.log(event.currentTarget)
                event.currentTarget.classList.add('user-nav-lang__list-item--active')
            } else if (event.currentTarget.id == 2) {
                this.idOfActiveLang = 2
                for (let item of document.querySelectorAll('.user-nav-lang__list-item')) {
                    item.classList.remove('user-nav-lang__list-item--active')
                }
                event.currentTarget.classList.add('user-nav-lang__list-item--active')
            } else if (event.currentTarget.id == 3) {
                this.idOfActiveLang = 3
                for (let item of document.querySelectorAll('.user-nav-lang__list-item')) {
                    item.classList.remove('user-nav-lang__list-item--active')
                }
                event.currentTarget.classList.add('user-nav-lang__list-item--active')
            }
            this.langsMenuIsActive = false
        },
        headerToActive() {
            document.querySelector('.header').classList.toggle('full-header--active')
        },
        openCurrencyWindow() {
            if (this.currencyWindowOpen == false) {
                this.currencyWindowOpen = true
                document.querySelector('.header-mobile-bottom-currency').classList.add('header-mobile-bottom-currency--active')
            } else {
                this.currencyWindowOpen = false
                document.querySelector('.header-mobile-bottom-currency').classList.remove('header-mobile-bottom-currency--active')
            }
        },
        eurToActive() {
            this.activeCurrency = 'Eur'
            this.currencyWindowOpen = false
            document.querySelector('.header-mobile-bottom-currency').classList.remove('header-mobile-bottom-currency--active')
        },
        dolToActive() {
            this.activeCurrency = 'Dol'
            this.currencyWindowOpen = false
            document.querySelector('.header-mobile-bottom-currency').classList.remove('header-mobile-bottom-currency--active')
        },
        subMenuToActive() {
            this.subMenuIsActive = true
        },
        subMenuToDisable() {
            this.subMenuIsActive = false
        },
        menuToDisable() {
            this.menuIsActive = false
            document.querySelector('.body').classList.remove('mobile-menu--active')
        },
        menuToActive() {
            this.menuIsActive = true
            document.querySelector('.body').classList.add('mobile-menu--active')
        }
    },
    mounted() {
        document.addEventListener("click", (event) => {
            console.log(event.target.classList)
            if (event.target.classList) {
                if (event.target.classList != 'header', event.target.classList != 'menu-list__btn') {
                    document.querySelector('.header').classList.remove('full-header--active')
                }
            }
        })
        if (this.headerIsFull == true) {
            document.querySelector('.header').classList.add('full-header')
        }
    }
}
</script>

